% ylim(axes1,[0 1]);
% Uncomment the following line to preserve the Z-limits of the axes
% zlim(axes1,[0 1]);
view(axes1,[130 55]);
grid(axes1,'on');
% Set the remaining axes properties
set(axes1,'ZDir','reverse');
% Create axes
axes2 = axes('Parent',figure1,...
    'Position',[0.570340909090909 0.11 0.334659090909091 0.793174606123579]);
hold(axes2,'on');

% Create surf
surf(xdata2,ydata2,zdata2,'Parent',axes2,'LineStyle','none');

